import React, { Component } from 'react';
import { Fabric } from 'office-ui-fabric-react/lib/Fabric';
import { DefaultButton } from 'office-ui-fabric-react/lib/Button';
import XLSX from 'xlsx';
import { make_cols } from './MakeColumns';
import { SheetJSFT } from './types';
import {GridComponent, ColumnDirective, ColumnsDirective,
  Page, Inject, Filter, gridObserver} from '@syncfusion/ej2-react-grids';

function App() {

    // Table data
    var eqtableData = null;


     var file = {};
     var data = [];
     var cols = [];

     const groupBy = require('json-groupby');

     const selectionOptions = {
      type: 'Multiple'
    };

    let grid = null;
    constÂ rowSelected = () => { 
      if(grid){
        const selectedRowIndexes = grid.getSelectedRecords();
        console.log(selectedRowIndexes);
      }
    }

  function renderChart() {
    var Chart = require('chart.js');
    const ctx = document.getElementById('chart').getContext('2d');
    const myChart = new Chart(ctx, {
    type: 'bar',
    data: {
        labels: ['Red', 'Blue', 'Yellow', 'Green', 'Purple', 'Orange'],
        datasets: [{
            label: '# of Votes',
            data: [12, 19, 3, 5, 2, 3],
            backgroundColor: [
                'rgba(255, 99, 132, 0.2)',
                'rgba(54, 162, 235, 0.2)',
                'rgba(255, 206, 86, 0.2)',
                'rgba(75, 192, 192, 0.2)',
                'rgba(153, 102, 255, 0.2)',
                'rgba(255, 159, 64, 0.2)'
            ],
            borderColor: [
                'rgba(255, 99, 132, 1)',
                'rgba(54, 162, 235, 1)',
                'rgba(255, 206, 86, 1)',
                'rgba(75, 192, 192, 1)',
                'rgba(153, 102, 255, 1)',
                'rgba(255, 159, 64, 1)'
            ],
            borderWidth: 1
        }]
    },
    options: {
        scales: {
            yAxes: [{
                ticks: {
                    beginAtZero: true
                }
            }]
        }
    }
});
  }


  function download(content, fileName, contentType) {
    var a = document.createElement("a");
    var file = new Blob([content], {type: contentType});
    a.href = URL.createObjectURL(file);
    a.download = fileName;
    a.click();
}

 function handleChange(e) {
    const files = e.target.files;
    if (files && files[0]) file = files[0];
  };

 function handleFile() {
    /* Boilerplate to set up FileReader */
    const reader = new FileReader();
    const rABS = !!reader.readAsBinaryString;

    reader.onload = (e) => {
      const bstr = e.target.result;
      const wb = XLSX.read(bstr, { type: rABS ? 'binary' : 'array', bookVBA : true });
      const wsname = wb.SheetNames[0];
      const ws = wb.Sheets[wsname];
      data = XLSX.utils.sheet_to_json(ws);
      cols = make_cols(ws['!ref'])

        // Refactor data
        const strData = JSON.stringify(data, null, 2)
        eqtableData = strData;
        var jsonObject = JSON.parse(strData);
        var i;
        for(i = 0; i < jsonObject.length; i++){
          if (!("Part Number" in jsonObject[i] && "Code" in jsonObject[i] && "Date" in jsonObject[i])) {
            delete jsonObject[i];
          }
          else {
            if(typeof jsonObject[i].Code !== 'number') {
              delete jsonObject[i];
            }
          }
        }

        console.log(groupBy(jsonObject, ['Equipment ID', 'Part Number']))
        renderChart();

    };
    if (rABS) {
      reader.readAsBinaryString(file);
    } else {
      reader.readAsArrayBuffer(file);
    };
  }

  // Load table
  function loadInTable() {
    if(eqtableData !== null) {
      return <div id="parent">
      <div id="EqID" style={{ marginTop: '20%', margin:'10%'}}>
        <GridComponent dataSource={eqtableData}  
          allowPaging={true}
          pageSettings={{pageSize:10}}
          allowFiltering={true}
          rowSelected={rowSelected}
          ref={g => grid = g}
          >
          <ColumnsDirective>
        <ColumnDirective field='Equipment ID' headerText='Equipment List' textAlign='Center'/>
        </ColumnsDirective>
        <Inject services={[Page, Filter]}/>
        </GridComponent>
      </div>,

      <div id="PID" style={{ marginTop: '20%', margin:'10%'}}>
      <GridComponent dataSource={data}
      allowPaging={true}
      pageSettings={{pageSize:10}}
      allowFiltering={true}
      //selectionSettings={selectionOptions}
      >
        <ColumnsDirective>
        <ColumnDirective type='checkbox' width='50'/>
      <ColumnDirective field='Part Number' headerText='Part Number' textAlign='Center'/>
      </ColumnsDirective>
      <Inject services={[Page, Filter]}/>
      </GridComponent>
      </div> 

      <div id="RC" style={{ marginTop: '20%', margin:'10%'}}>
      <GridComponent dataSource={data}
      allowPaging={true}
      pageSettings={{pageSize:10}}
      allowFiltering={true}
      //selectionSettings={selectionOptions}
      >
        <ColumnsDirective>
        <ColumnDirective type='checkbox' width='50'/>
      <ColumnDirective field='Code' headerText='Repair Code' textAlign='Center'/>
      </ColumnsDirective>
      <Inject services={[Page, Filter]}/>
      </GridComponent>
      </div>

    </div>
    }
    else{
      return (<div>empty</div>)
    }
  }


    return (
      <div>
        <label htmlFor="file">Upload your Excel File</label>
        <br />
        <input type="file" className="form-control" id="file" accept={SheetJSFT} onChange={handleChange} />
        <br />
        <input type='submit' 
          value="Update"
          onClick={handleFile} />
        <div>
        <loadInTable/>
        </div>
        <div>
          <canvas id="chart" width="400" height="400"></canvas>
        </div>
      </div>       

    )
  }


export default App;
